import {useRouter} from "next/router";
import Header from "../components/Header";
import React from "react";
import axios from 'axios'

export async function getServerSideProps(context){
    //handle if person or business url
    //assume business url
    let page = context.query.page;
    let data = [];
    let pageUrl = "http://www.facebook.com/" + page;
    axios.get(pageUrl).then((res)=>{
        const DomParser = require('dom-parser');
        const parser = new DomParser();
        const doc = parser.parseFromString(res.data,'text/html');
        const sidebarContainer = doc.getElementById("entity_sidebar");
        let sidebarList = sidebarContainer.querySelectorAll("[href]");
        console.log(sidebarList)
        for(const item of sidebarList){
            data.push({text: item.innerText, link: item.href});
        }
    })
    return{props:{data}}
}

const Page = ({data}) => {
    const router = useRouter();

    console.log(data);
    const sidebar = data.map((item) => {
        let text = item.text;
        let link = item.link;
        return(
            <li key={link}>
                <a href={link}>{text}</a>
            </li>
        )
    });


    return (
        <div>
            <Header />
            <div>
                <h1>{router.query.page}</h1>
                <ul>
                    {sidebar}
                </ul>
            </div>
        </div>
    );
};

export default Page;
