import {useRouter} from "next/router";
import Header from "../components/Header";
import React from "react";
import axios from 'axios'

export async function getServerSideProps(context){
    //handle if person or business url
    //assume business url
    let page = context.query.page;
    let data = [];
    let pageUrl = "http://www.facebook.com/" + page;
    axios.get(pageUrl).then((res)=>{
        const DomParser = require('dom-parser');
        const parser = new DomParser();
        const doc = parser.parseFromString(res.data);
        const sidebarContainer = doc.getElementById("entity_sidebar");

        let sidebarList = sidebarContainer.childNodes[2].childNodes;
        //console.log(sidebarList)
        for(const item of sidebarList){
            console.log(item.childNodes[0].childNodes[0].innerHTML)
            console.log(item.childNodes[0].attributes)
            data.push({text: item.innerText, link: item.href});
            console.log("next item")
        }
    })
    return{props:{data}}
}

const Page = ({data}) => {
    const router = useRouter();

    console.log(data);
    const sidebar = data.map((item) => {
        let text = item.text;
        let link = item.link;
        return(
            <li key={link}>
                <a href={link}>{text}</a>
            </li>
        )
    });


    return (
        <div>
            <Header />
            <div>
                <h1>{router.query.page}</h1>
                <ul>
                    {sidebar}
                </ul>
            </div>
        </div>
    );
};

export default Page;
