import MakeAvatar from "./MakeAvatar";
import React from "react";
import Card from "@material-ui/core/Card";
import CardActionArea from "@material-ui/core/CardActionArea";
import CardMedia from "@material-ui/core/CardMedia";
import CardContent from "@material-ui/core/CardContent";
import Typography from "@material-ui/core/Typography";
import CardActions from "@material-ui/core/CardActions";
import Button from "@material-ui/core/Button";
import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles({
    root: {
        maxWidth: 750,
    },
});

export default function MakePosts(postsArray) {
    const classes = useStyles();

    const postsList = postsArray.map((post) => {
        let Avatar = MakeAvatar('/');
        const paragraphs = post.text.map((paragraph) => {
            return (
                <Typography variant="body2" color="textSecondary" component="p">
                    {paragraph}
                </Typography>
            )
        });
        console.log(post.img)
        let images = post.img.map((source) => {
            if (source.includes('p50x50')) {
                Avatar = MakeAvatar(source)
            } else {
                //console.log(source)
                return (
                    source
                )
            }
        });
        images = images.splice(0,1);
        return (
            <Card className={classes.root}>
                <CardActionArea>
                    <CardMedia
                        component="img"
                        alt="Post Image"
                        height="250"
                        image={images[0]}
                        title="Post Image"
                    />
                    <CardContent>
                        <div>
                            {Avatar}
                            <Typography gutterBottom variant="h5" component="h2">
                                User Name
                            </Typography>
                        </div>
                        {paragraphs}
                    </CardContent>
                </CardActionArea>
                <CardActions>
                    <Button size="small" color="primary">
                        Share
                    </Button>
                    <Button size="small" color="primary">
                        Likes
                    </Button>
                </CardActions>
            </Card>
        )
    })
    return postsList
}
