import {useRouter} from "next/router";
import React from "react";
import GetPosts from "../../components/Business/GetPosts";
import HeaderBar from "../../components/HeaderBar";
import MakeAvatar from "../../components/MakeAvatar";

export async function getServerSideProps(context){
    let page = context.query.page;
    let postsArray = await GetPosts(page);

    return{props:{postsArray}}
}

const Page = ({postsArray}) => {
    const router = useRouter();

    const postsList = postsArray.map((post) => {
        let Avatar = MakeAvatar('/');
        const paragraphs = post.text.map((paragraph) => {
            return(
                <p>{paragraph}</p>
            )
        });
        const images = post.img.map((source) => {
            if(source.includes('p50x50')){
                console.log('avatar');
                Avatar = MakeAvatar(source)
            }
            return(
                <img src={source}/>
            )
        });
        return(
            <li>
                {Avatar}
                <div>
                    {paragraphs}
                </div>
                <div>
                    {images}
                </div>
            </li>
        )
    });

    return (
        <div>
            <HeaderBar/>
            <div>
                <h1>{router.query.page}</h1>
                <ul>
                    {postsList}
                </ul>
            </div>
        </div>
    );
};

export default Page;
