export default function GetSidebar(doc){
    const sidebarContainer = doc.getElementById("entity_sidebar");
    console.log(sidebarContainer);
    const sidebarList = sidebarContainer.childNodes[2].childNodes;
    let data = [];
    for(const item of sidebarList){
        let optionText = item.childNodes[0].childNodes[0].innerHTML;
        let optionLink = item.childNodes[0].attributes[2].value;
        //Symbol correction
        if(optionText.includes("&amp;")){
            optionText = optionText.replace("&amp;","&")
        }
        //push to object array that gets passed as props to server
        data.push({text: optionText, link: optionLink});
    }
    return data
}
