import {useRouter} from "next/router";
import Header from "../components/Header";
import React from "react";
import axios from 'axios'
import GetSideBar from "../components/Business/GetSideBar";
import GetFeed from "../components/Business/GetFeed";

export async function getServerSideProps(context){
    //handle if person or business url
    //assume business url

    //axios request for page
    let page = context.query.page;
    let pageUrl = "http://www.facebook.com/" + page;
    const res = await axios.get(pageUrl);
    const DomParser = require('dom-parser');
    const parser = new DomParser();
    const doc = parser.parseFromString(res.data);

    //pass html document to functions to get various page elements
    let sidebarLinks = await GetSideBar(doc);
    //right sidebar PagesProfileHomeSecondaryColumnPagelet
    let mainFeed = await GetFeed(doc);
    console.log(mainFeed)

    return{props:{sidebarLinks, mainFeed}}
}

const Page = ({sidebarLinks, mainFeed}) => {
    const router = useRouter();

    const parser = new DOMParser();
    const feed = parser.parseFromString(mainFeed, 'text/html');
    console.log(feed)

    const sidebar = sidebarLinks.map((item) => {
        let text = item.text;
        let link = item.link;
        return(
            <li key={link}>
                <a href={link}>{text}</a>
            </li>
        )
    });

    return (
        <div>
            <Header />
            <div>
                <h1>{router.query.page}</h1>
                <ul>
                    {sidebar}
                </ul>
            </div>
        </div>
    );
};

export default Page;
