import {useRouter} from "next/router";
import Header from "../../components/Header";
import React from "react";
import axios from 'axios'

export async function getServerSideProps(context){
    //handle if person or business url
    //assume business url
    let data = context.query.page;
    return{props:{data}}
}

const Page = ({data}) => {
    const router = useRouter();

    console.log(data);
    let sidebar = <div/>;

    let pageUrl = "http://www.facebook.com/" + router.query.page;
    axios.get(pageUrl).then((res)=>{
        const parser = new DOMParser();
        const doc = parser.parseFromString(res.data,'text/html');
        sidebar = doc.getElementById("entity_sidebar");
        let sidebarLinks = sidebar.getElementsByTagName("a")
        console.log(sidebarLinks)
    })

    return (
        <div>
            <Header />
            <div>
                <h1>{router.query.page}</h1>
                {sidebar}
            </div>
        </div>
    );
};

export default Page;
