import React from 'react'
import { useRouter } from 'next/router';
import Header from "../../../components/Header";
//import fetch from 'node-fetch'
import axios from 'axios'

export async function getServerSideProps() {
    //make and await axios request on public search page
    let query = "Patrick";
    if(query === undefined){
        return
    }
    console.log("searchquery: ",query)
    const searchUrl = 'https://www.facebook.com/public/'+ query;

    let header = {
        'Access-Control-Allow-Origin':'*',
        'Content-Type': 'multipart/form-data'
    };

    let res = await axios.get(searchUrl);
        let htmlText = await res.data;
        //Need parser import for server side rendering
        var DomParser = require('dom-parser');
        var parser = new DomParser();
        var dom = parser.parseFromString(htmlText);

        if(dom !== null){
            //Results list is hidden element on Dom as string (lazy loading?) need to parse
            var hiddenElements = dom.getElementsByClassName('hidden_elem');
            var listText1 = hiddenElements[1].childNodes[0].childNodes[1].innerHTML;
            var listText2 = hiddenElements[4].childNodes[0].childNodes[1].innerHTML;
            //console.log(listText1)
            var resultsContainer1 = parser.parseFromString(listText1);
            var resultsContainer2 = parser.parseFromString(listText1);

            //console.log(resultsContainer1)
            var resultsArray1 = resultsContainer1.getElementsByClassName("_4bl7 _3-90")
            var resultsArray2 = resultsContainer2.getElementsByClassName("_4bl7 _3-90")

            console.log(resultsArray1[0].innerHTML);

            //name, picture, href profile
            let results = [{name: 'Patrick Evans',img: 'testlink.com',link:"google.com"},]
            return{props: {results}}
        }
}



const Page = ({results}) => {
    const router = useRouter();

    //iterate through array set list

    //pic
    //name
    //link

    const resultsList = <li>"test"</li>;

    return (
        <div>
            <Header />
            <div>
                <div>Search Results:</div>
                <ul>
                    {resultsList}
                </ul>
            </div>
        </div>
    );
};

export default Page;
