import {useRouter} from "next/router";
import Header from "../components/Header";
import React from "react";
import axios from 'axios'

export async function getServerSideProps(context){
    //handle if person or business url
    //assume business url
    let data = context.query.page;
    return{props:{data}}
}

const Page = ({data}) => {
    const router = useRouter();

    console.log(data);

    let sidebarLinks = [];

    let pageUrl = "http://www.facebook.com/" + router.query.page;
    axios.get(pageUrl).then((res)=>{
        const parser = new DOMParser();
        const doc = parser.parseFromString(res.data,'text/html');
        const sidebar = doc.getElementById("entity_sidebar");
        let sidebarList = sidebar.querySelectorAll("[href]");
        for(const item of sidebarList){
            sidebarLinks.push(item.href);
        }
    })
    console.log(sidebarLinks)

    return (
        <div>
            <Header />
            <div>
                <h1>{router.query.page}</h1>
            </div>
        </div>
    );
};

export default Page;
