import {useRouter} from "next/router";
import React from "react";
import GetPosts from "../../components/Business/GetPosts";

export async function getServerSideProps(context){
    let page = context.query.page;
    let postsArray = await GetPosts(page);

    return{props:{postsArray}}
}

const Page = ({postsArray}) => {
    const router = useRouter();

    const postsList = postsArray.map((post) => {
        const paragraphs = post.text.map((paragraph) => {
            return(
                <p>{paragraph}</p>
            )
        });
        const images = post.img.map((source) => {
            return(
                <img src={source}/>
            )
        });
        return(
            <li>
                <div>
                    {paragraphs}
                </div>
                <div>
                    {images}
                </div>
            </li>
        )
    });

    return (
        <div>
            <div>
                <h1>{router.query.page}</h1>
                <ul>
                    {postsList}
                </ul>
            </div>
        </div>
    );
};

export default Page;
