import React from 'react'
import { useRouter } from 'next/router';
import Header from "../../../components/Header";
import fetch from 'node-fetch'

export async function getServerSideProps(props) {
    //make and await axios request on public search page
    let query = props.query;
    if(query === undefined){
        return
    }
    console.log("searchquery: ",query)
    const searchUrl = 'https://www.facebook.com/public/'+ query;

    fetch(searchUrl).then((res)=>{
        let htmlText = res;
        console.log(htmlText)
        var parser = new DOMParser();
        var htmlDoc = parser.parseFromString(htmlText, 'text/html');
        if(htmlDoc !== null){
            //Results list is hidden element on Dom as string (lazy loading?) need to parse
            var textUnformatted = htmlDoc.getElementById("u_0_n").innerHTML;
            var textFormatted = textUnformatted.replace("<!-- ","");
            var resultsContainer = parser.parseFromString(textFormatted, 'text/html');
            var resultsArray = resultsContainer.getElementsByClassName("_2ial")

            //console.log(resultsArray);
            return{props: resultsArray}
        }
    });
}



const Page = () => {
    const router = useRouter();

    console.log(this.props.resultsArray)

    return (
        <div>
            <Header />
            <div>
                <div>Search Results:</div>

            </div>
        </div>
    );
};

export default Page;
