import fetch from "node-fetch";

export default async function GetTrueVideoSource(videoUrl){

    //fetch video page
    var requestOptions = {
        method: 'GET',
        redirect: 'follow'
    };

    console.log('fetching')

    const res = await fetch(videoUrl, requestOptions);
    const resText = await res.text();
    const videoSource = await resText.split("video:[{url:\"")[1].split("\",start:0")[0];
    console.log("second source: ",videoSource);
    return videoSource
}
