import Link from "next/link";
import React from "react";

class SearchBar extends React.Component {
    constructor(props) {
        super(props);
        this.state = {query: ''};
    }

    handleChange(e){
        this.setState({query: e.target.value})
    }

    getQuery(){
        console.log("clicked!")
    }

    render() {
        return(
            <div>
                <div>
                    <h3> Search People</h3>
                    <input type='text' value={this.state.query} onChange={(event => this.handleChange(event))}
                    />
                    <button id='searchButton' onClick={()=>{this.getQuery}}>
                        <Link href={`/fb/public/${this.state.query}`}>
                            <a> Go </a>
                        </Link>
                    </button>
                </div>
            </div>
        )
    }
}

export default SearchBar
