import React from 'react'
import { useRouter } from 'next/router';
import Header from "../../../components/Header";
//import fetch from 'node-fetch'
import axios from 'axios'

export async function getServerSideProps() {
    //make and await axios request on public search page
    let query = "Patrick";
    if(query === undefined){
        return
    }
    console.log("searchquery: ",query)
    const searchUrl = 'https://www.facebook.com/public/'+ query;

    let header = {
        'Access-Control-Allow-Origin':'*',
        'Content-Type': 'multipart/form-data'
    };

    let res = await axios.get(searchUrl);
        let htmlText = await res.data;
        //Need parser import for server side rendering
        var DomParser = require('dom-parser');
        var parser = new DomParser();
        var dom = parser.parseFromString(htmlText);

        if(dom !== null){
            //Results list is hidden element on Dom as string (lazy loading?) need to parse
            var listText = dom.getElementById('u_0_f').childNodes[1].innerHTML;
            //console.log(listText.childNodes[1].innerHTML);
            var resultsContainer = parser.parseFromString(listText);
            //console.log(resultsContainer.rawHTML);
            var resultsArray = resultsContainer.getElementsByClassName("_2ial")

            console.log(resultsArray);
            var resultsArray = "test";

            return{props: {resultsArray}}
        }
}



const Page = ({resultsArray}) => {
    const router = useRouter();

    //iterate through array set list
    const resultsList = <li>{resultsArray}</li>;

    return (
        <div>
            <Header />
            <div>
                <div>Search Results:</div>
                <ul>
                    {resultsList}
                </ul>
            </div>
        </div>
    );
};

export default Page;
