import {useRouter} from "next/router";
import React from "react";
import GetPosts from "../../components/Business/GetPosts";

export async function getServerSideProps(context){
    let page = context.query.page;
    let postsArray = await GetPosts(page);
    let postsTextArray = [];
    let postsImages = [];
    //console.log(postsArray)
    for(const post of postsArray){
        //console.log(post)
        postsTextArray.push(post.text);
        postsImages.push(post.img);
    }

    return{props:{postsTextArray, postsImages}}
}

const Page = ({postsTextArray, postImages}) => {
    const router = useRouter();

    //console.log(postsArray);


    const postsList = postsArray.map((post) => {
        //console.log(post);

        const paragraphs = post.map((paragraph) => {
            return(
                <p>{paragraph}</p>
            )
        });
        return(
            <li>
                {paragraphs}
            </li>
        )
    });

    return (
        <div>
            <div>
                <h1>{router.query.page}</h1>
                <ul>
                    {postsList}
                </ul>
            </div>
        </div>
    );
};

export default Page;
