import {useRouter} from "next/router";
import Header from "../components/Header";
import React from "react";
import axios from 'axios'

export async function getServerSideProps(context){
    //handle if person or business url
    //assume business url
    let data = context.query.page;
    return{props:{data}}
}

const Page = ({data}) => {
    const router = useRouter();

    //put this block on server
    let sidebardata = [];
    let pageUrl = "http://www.facebook.com/" + router.query.page;
    axios.get(pageUrl).then((res)=>{
        const parser = new DOMParser();
        const doc = parser.parseFromString(res.data,'text/html');
        const sidebarContainer = doc.getElementById("entity_sidebar");
        let sidebarList = sidebarContainer.querySelectorAll("[href]");
        console.log(sidebarList)
        for(const item of sidebarList){
            sidebardata.push({text: item.innerText, link: item.href});
        }
    })
    console.log(sidebardata);

    console.log(data);
    const sidebar = sidebardata.map((item) => {
        let text = item.text;
        let link = item.link;
        return(
            <li key={link}>
                <a href={link}>{text}</a>
            </li>
        )
    });


    return (
        <div>
            <Header />
            <div>
                <h1>{router.query.page}</h1>
                <ul>
                    {sidebar}
                </ul>
            </div>
        </div>
    );
};

export default Page;
