export default async function GetPosts(page){
    const jsdom = require("jsdom");
    const { JSDOM } = jsdom;

    const pageurl = 'https://www.facebook.com/' + page + '/posts';

    let res = await axios.get(pageurl);
    const dom = new JSDOM(res.data);
    const document = dom.window.document;

    const container = document.getElementById("content_container");
    if(container) {
        const postArray = document.getElementsbyClassName("userContentWrapper");
        console.log(postArray.length);
        for (const post in postArray) {
            //console.log(post)
            return []
        }
    }
    else{
            return []
    }
}
